EventDataMap.typeDefinition:
{
  "kind": "map",
  "description": "A map of event data indexed by event ID",
  "keyType": "string",
  "valueType": {
    "$ref": "SomeEvent"
  }
}

EventKind.typeDefinition:
{
  "kind": "enum",
  "description": "All the available event topics",
  "values": [
    {
      "Value": "data.created",
      "Description": "Data created"
    },
    {
      "Value": "data.updated",
      "Description": "Data updated"
    }
  ]
}

MethodKind.typeDefinition:
{
  "kind": "enum",
  "description": "All the available RPC methods",
  "values": [
    {
      "Value": "ping",
      "Description": "Ping"
    },
    {
      "Value": "subscribe",
      "Description": "Subscribe"
    },
    {
      "Value": "unsubscribe",
      "Description": "Unsubscribe"
    },
    {
      "Value": "user.create",
      "Description": "Create user"
    },
    {
      "Value": "user.update",
      "Description": "Update user"
    },
    {
      "Value": "user.delete",
      "Description": "Delete user"
    },
    {
      "Value": "user.list",
      "Description": "List users"
    },
    {
      "Value": "user.get",
      "Description": "Get user"
    }
  ]
}

PingResult.typeDefinition:
{
  "kind": "object",
  "description": "Result for the Ping method",
  "fields": [
    {
      "name": "message",
      "description": "A message describing the result",
      "type": "string"
    },
    {
      "name": "status",
      "description": "The status of the ping",
      "$ref": "PingStatus"
    }
  ]
}

SomeEvent.typeDefinition:
{
  "kind": "object",
  "description": "Result for the SomeEvent method",
  "fields": [
    {
      "name": "id",
      "description": "The unique identifier for the result",
      "type": "string",
      "format": "uuid"
    }
  ]
}

StringMap.typeDefinition:
{
  "kind": "map",
  "description": "A map with string values for storing key-value pairs",
  "keyType": "string",
  "valueType": {
    "type": "string"
  }
}

SubscribeParams.typeDefinition:
{
  "kind": "object",
  "description": "Parameters for the Subscribe method",
  "fields": [
    {
      "name": "event",
      "description": "The event topic to subscribe to",
      "$ref": "EventKind"
    }
  ]
}

SubscribeResult.typeDefinition:
{
  "kind": "object",
  "description": "Result for the Subscribe method",
  "fields": [
    {
      "name": "success",
      "description": "Whether the subscribe was successful",
      "type": "boolean"
    }
  ]
}

UnsubscribeParams.typeDefinition:
{
  "kind": "object",
  "description": "Parameters for the Unsubscribe method",
  "fields": [
    {
      "name": "event",
      "description": "The event topic to unsubscribe from",
      "$ref": "EventKind"
    }
  ]
}

UnsubscribeResult.typeDefinition:
{
  "kind": "object",
  "description": "Result for the Unsubscribe method",
  "fields": [
    {
      "name": "success",
      "description": "Whether the unsubscribe was successful",
      "type": "boolean"
    }
  ]
}
